import test from "ava";
import { Contract, ContractFactory } from "ethers";
import {
  AbiItems,
  DEFAULT_ETH_TO_CKB_SUDT_DECIMAL,
  PolyjuiceConfig,
} from "@polyjuice-provider/base";
import {
  PolyjuiceWallet,
  PolyjuiceJsonRpcProvider,
  PolyjuiceWebsocketProvider,
} from "../lib/index";
import crypto from "crypto";
import Web3 from "web3";
import errorReceiptContract from "../../../contract-testcase/ErrorReceipt.json";

const root = require("path").join.bind(this, __dirname, "..");
require("dotenv").config({ path: root(".test.env") });

const SimpleStorageV2_ByteCode =
  "";
const SimpleStorageV2_Abi = [
  {
    constant: false,
    inputs: [
      {
        internalType: "address",
        name: "newValue",
        type: "address",
      },
    ],
    name: "set",
    outputs: [],
    payable: false,
    stateMutability: "nonpayable",
    type: "function",
  },
  {
    constant: true,
    inputs: [],
    name: "get",
    outputs: [
      {
        internalType: "address",
        name: "",
        type: "address",
      },
    ],
    payable: false,
    stateMutability: "view",
    type: "function",
  },
  {
    constant: false,
    inputs: [
      {
        internalType: "address[]",
        name: "newValue",
        type: "address[]",
      },
    ],
    name: "setArray",
    outputs: [],
    payable: false,
    stateMutability: "nonpayable",
    type: "function",
  },
  {
    constant: true,
    inputs: [],
    name: "getArray",
    outputs: [
      {
        internalType: "address[]",
        name: "",
        type: "address[]",
      },
    ],
    payable: false,
    stateMutability: "view",
    type: "function",
  },
];
let provider: PolyjuiceJsonRpcProvider;
let wsProvider: PolyjuiceWebsocketProvider;
let deployer: PolyjuiceWallet;
let wsDeployer: PolyjuiceWallet;
let SimpleStorageV2_Address = process.env.EXAMPLE_CONTRACT_ADDRESS;

let ethAddressForPrivateKey = process.env.ETH_ADDRESS;
let notExistAddress = genNewEthAddress();
let notExistAddressForWs = genNewEthAddress();
let testAddressArray = [
  genNewEthAddress(),
  "0x0000000000000000000000000000000000000000",
  ethAddressForPrivateKey,
];

function genNewEthAddress() {
  return Web3.utils.toChecksumAddress(crypto.randomBytes(20).toString("hex"));
}

test.before((t) => {
  // init provider and web3
  const web3Rpc = process.env.WEB3_JSON_RPC;
  const web3WsRpc = process.env.WEB3_WS_JSON_RPC;
  const polyjuiceConfig: PolyjuiceConfig = {
    abiItems: SimpleStorageV2_Abi as AbiItems,
    web3Url: web3Rpc,
  };
  provider = new PolyjuiceJsonRpcProvider(polyjuiceConfig, web3Rpc);
  deployer = new PolyjuiceWallet(
    process.env.PRIVATE_KEY,
    polyjuiceConfig,
    provider
  );
  wsProvider = new PolyjuiceWebsocketProvider(polyjuiceConfig, web3WsRpc);
  wsDeployer = new PolyjuiceWallet(
    process.env.PRIVATE_KEY,
    polyjuiceConfig,
    wsProvider
  );
});

test.serial("import class", (t) => {
  t.not(provider, undefined);
  t.not(deployer, undefined);
});

test.serial("deploy_example_contract", async (t) => {
  const implementationFactory = new ContractFactory(
    SimpleStorageV2_Abi,
    SimpleStorageV2_ByteCode,
    deployer
  );

  const tx = implementationFactory.getDeployTransaction();
  tx.gasPrice = 0;
  tx.gasLimit = 500000;
  const res = await deployer.sendTransaction(tx);
  const txReceipt = await res.wait();
  t.is(txReceipt.contractAddress.slice(0, 2), "0x");
  SimpleStorageV2_Address = txReceipt.contractAddress;
  testAddressArray.push(SimpleStorageV2_Address);
});

test.serial("call set not-exist address on contract", async (t) => {
  const simpleStorageV2 = new Contract(
    SimpleStorageV2_Address,
    SimpleStorageV2_Abi,
    deployer
  );
  const res = await simpleStorageV2.set(notExistAddress);
  t.is(typeof res.wait, "function");
  const txReceipt = await res.wait();
  t.not(txReceipt, undefined);
});

test.serial("call contract get for not-exist address", async (t) => {
  const simpleStorageV2 = new Contract(
    SimpleStorageV2_Address,
    SimpleStorageV2_Abi,
    deployer
  );

  const address = await simpleStorageV2.callStatic.get();
  t.is(address, notExistAddress);
});

test.serial("call set array address on contract", async (t) => {
  const simpleStorageV2 = new Contract(
    SimpleStorageV2_Address,
    SimpleStorageV2_Abi,
    deployer
  );
  const res = await simpleStorageV2.setArray(testAddressArray);
  t.is(typeof res.wait, "function");
  const txReceipt = await res.wait();
  t.not(txReceipt, undefined);
  t.is(txReceipt.transactionHash.slice(0, 2), "0x");
});

test.serial("call contract get array address", async (t) => {
  const simpleStorageV2 = new Contract(
    SimpleStorageV2_Address,
    SimpleStorageV2_Abi,
    deployer
  );

  const address_array = await simpleStorageV2.callStatic.getArray();
  t.deepEqual(address_array, testAddressArray);
});

test.serial("ws-provider: call set address on contract", async (t) => {
  const simpleStorageV2 = new Contract(
    SimpleStorageV2_Address,
    SimpleStorageV2_Abi,
    wsDeployer
  );
  const res = await simpleStorageV2.set(notExistAddressForWs);
  t.is(typeof res.wait, "function");
  const txReceipt = await res.wait();
  t.not(txReceipt, undefined);
});

test.serial("ws-provider: call contract get_address", async (t) => {
  const simpleStorageV2 = new Contract(
    SimpleStorageV2_Address,
    SimpleStorageV2_Abi,
    wsDeployer
  );

  const address = await simpleStorageV2.callStatic.get();
  t.is(address, notExistAddressForWs);
});

test.serial("test_error_receipt_contract", async (t) => {
  const implementationFactory = new ContractFactory(
    errorReceiptContract.abi,
    errorReceiptContract.bytecode,
    deployer
  );

  const tx = implementationFactory.getDeployTransaction();
  tx.gasPrice = 0;
  tx.gasLimit = 500000;
  const res = await deployer.sendTransaction(tx);
  const txReceipt = await res.wait();
  t.is(txReceipt.contractAddress.slice(0, 2), "0x");

  const contract = new Contract(
    txReceipt.contractAddress,
    errorReceiptContract.abi,
    deployer
  );
  const callRevert = async () => {
    try {
      await contract.callStatic.getRevertMsg(555);
    } catch (error) {
      throw new Error(error.message);
    }
  };
  const callRevertRunResult = await t.throwsAsync(callRevert);
  t.is(callRevertRunResult.message, "revert: you trigger crying value!");
  const sendRevert = async () => {
    try {
      const callRevertRes = await contract.getRevertMsg(555);
      await callRevertRes.wait(1);
    } catch (error) {
      throw new Error(error.message);
    }
  };
  const callRevertRes = await t.throwsAsync(sendRevert);
  t.is(callRevertRes.message, "revert: you trigger crying value!");
});

